	function mouseOverHighlight(id) {

		defaultTextColor = $("#VideoTitle0" + id).css("color");

		$("#VideoTitle0" + id).css("background-color", "#296eff");

		$("#VideoTitle0" + id).css("color", "#FFFFFF");

	}



	function mouseOutHighlight(id) {

		if(id == still) {

			$("#VideoTitle0" + id).css("background-color", "#1297FF");

			$("#VideoTitle0" + id).css("color", "#FFFFFF");

		} else {

			$("#VideoTitle0" + id).css("background-color", "#EEEEEE");

			$("#VideoTitle0" + id).css("color", defaultTextColor);

		}

	}



	function onClickHighlight(id) {

		$("#VideoTitle0" + id).css("background-color", "#1297FF");

		$("#VideoTitle0" + id).css("color", "#FFFFFF");

		still = id;

		for(var i = 1; i <= maxVids; i++) {

			mouseOutHighlight(i);

		}

	}



	function hideStills() {

		for(var i = 1; i <= maxVids; i++) {

			$("#VideoGallery0" + i).css("display", "none");

		}

	}



	function fadeInStill(id) {

		$("#VideoGallery0" + id).fadeIn(fadeSpeed);

	}



	function changeVideo(video) {

		hideStills();

		fadeInStill(video);

		onClickHighlight(video);

		return false;

